patch:
diff --git a/hts.c b/hts.c
index 923eaaf0..86c7e8c0 100644
--- a/hts.c
+++ b/hts.c
@@ -4452,8 +4452,11 @@ int hts_itr_multi_next(htsFile *fd, hts_itr_t *iter, void *r)
                                 if ((max>>32) != tid)
                                     tid = HTS_IDX_START; // => no range limit

-                                if (end < rl->intervals[max & 0xffffffff].end)
-                                    end = rl->intervals[max & 0xffffffff].end;
+                uint32_t interval_idx = max & 0xffffffff;
+                if (interval_idx < rl->count) {
+                    if (end < rl->intervals[interval_idx].end)
+                        end = rl->intervals[interval_idx].end;
+                }
                                 if (v < iter->off[j].v)
                                     v = iter->off[j].v;
                                 j++;
