patch:
diff --git a/hts.c b/hts.c
index 923eaaf0..184efbae 100644
--- a/hts.c
+++ b/hts.c
@@ -4451,9 +4451,12 @@ int hts_itr_multi_next(htsFile *fd, hts_itr_t *iter, void *r)
                                 uint64_t max = iter->off[j].max;
                                 if ((max>>32) != tid)
                                     tid = HTS_IDX_START; // => no range limit
-
-                                if (end < rl->intervals[max & 0xffffffff].end)
-                                    end = rl->intervals[max & 0xffffffff].end;
+                                
+                                uint32_t intv_idx = max & 0xffffffff;
+                                if (intv_idx < rl->count) {
+                                    if (end < rl->intervals[intv_idx].end)
+                                        end = rl->intervals[intv_idx].end;
+                                }
                                 if (v < iter->off[j].v)
                                     v = iter->off[j].v;
                                 j++;
