patch:
diff --git a/icu4c/source/i18n/persncal.cpp b/icu4c/source/i18n/persncal.cpp
index f66ac676a4a..4b57cb0bf0d 100644
--- a/icu4c/source/i18n/persncal.cpp
+++ b/icu4c/source/i18n/persncal.cpp
@@ -213,15 +213,28 @@ void PersianCalendar::handleComputeFields(int32_t julianDay, UErrorCode &/*statu
     int32_t year, month, dayOfMonth, dayOfYear;

     int32_t daysSinceEpoch = julianDay - PERSIAN_EPOCH;
-    year = 1 + ClockMath::floorDivide(33 * daysSinceEpoch + 3, 12053);
+    // Prevent signed integer overflow by using int64_t for the calculation
+    int64_t temp = 33LL * static_cast<int64_t>(daysSinceEpoch) + 3;
+    year = 1 + static_cast<int32_t>(temp / 12053);
+    // Validate year range
+    if (year < -5000000 || year > 5000000) {
+        return;
+    }

     int32_t farvardin1 = 365 * (year - 1) + ClockMath::floorDivide(8 * year + 21, 33);
     dayOfYear = (daysSinceEpoch - farvardin1); // 0-based
+    // Validate dayOfYear range
+    if (dayOfYear < 0 || dayOfYear > 365) {
+        return;
+    }
     if (dayOfYear < 216) { // Compute 0-based month
         month = dayOfYear / 31;
     } else {
         month = (dayOfYear - 6) / 30;
     }
+    // Clamp month to valid range [0, 11] to prevent OOB access
+    if (month < 0) month = 0;
+    else if (month > 11) month = 11;
     dayOfMonth = dayOfYear - kPersianNumDays[month] + 1;
     ++dayOfYear; // Make it 1-based now
