patch:
diff --git a/icu4c/source/i18n/regexcmp.cpp b/icu4c/source/i18n/regexcmp.cpp
index a4c12804237..e739039c8db 100644
--- a/icu4c/source/i18n/regexcmp.cpp
+++ b/icu4c/source/i18n/regexcmp.cpp
@@ -4010,7 +4010,8 @@ UChar32  RegexCompile::peekCharLL() {
 //
 //------------------------------------------------------------------------------
 void RegexCompile::nextChar(RegexPatternChar &c) {
-
+    // Use iterative approach to avoid stack overflow from recursive calls
+    for (;;) {
     fScanIndex = UTEXT_GETNATIVEINDEX(fRXPat->fPattern);
     c.fChar    = nextCharLL();
     c.fQuoted  = FALSE;
@@ -4020,8 +4021,8 @@ void RegexCompile::nextChar(RegexPatternChar &c) {
         if ((c.fChar==chBackSlash && peekCharLL()==chE && ((fModeFlags & UREGEX_LITERAL) == 0)) ||
             c.fChar == (UChar32)-1) {
             fQuoteMode = FALSE;  //  Exit quote mode,
-            nextCharLL();        // discard the E
-            nextChar(c);         // recurse to get the real next char
+            nextCharLL();        // discard the E  
+            continue;            // iterate to get the real next char
         }
     }
     else if (fInBackslashQuote) {
@@ -4140,7 +4141,7 @@ void RegexCompile::nextChar(RegexPatternChar &c) {
                 //  "\Q"  enter quote mode, which will continue until "\E"
                 fQuoteMode = TRUE;
                 nextCharLL();       // discard the 'Q'.
-                nextChar(c);        // recurse to get the real next char.
+            continue;           // iterate to get the real next char.
             }
             else
             {
@@ -4158,6 +4159,8 @@ void RegexCompile::nextChar(RegexPatternChar &c) {
     fEOLComments = TRUE;

     // putc(c.fChar, stdout);
+    break;  // Exit the loop when we have a valid character
+    }
 }
