+            //
+            if (c.fChar == chBackSlash) {
+                int64_t pos = UTEXT_GETNATIVEINDEX(fRXPat->fPattern);
+                if (RegexStaticSets::gStaticSets->fUnescapeCharSet.contains(peekCharLL())) {
