+                               // \0 is not followed by any octal digits.
+                               error(U_REGEX_BAD_ESCAPE_SEQUENCE);
+                            }
+                            break;
+                        }
+                        c.fChar <<= 3;
+                        c.fChar += ch&7;
+                        if (c.fChar <= 255) {
+                            nextCharLL();
+                        } else {
+                            // The last digit made the number too big.  Forget we saw it.
+                            c.fChar >>= 3;
+                        }
+                    }
+                    c.fQuoted = TRUE;
+                }
+                else if (peekCharLL() == chQ) {
