diff --git a/icu4c/source/common/ustring.cpp b/icu4c/source/common/ustring.cpp
index 7ab2e1bf891..46677ac3e60 100644
--- a/icu4c/source/common/ustring.cpp
+++ b/icu4c/source/common/ustring.cpp
@@ -207,6 +207,15 @@ u_strchr(const UChar *s, UChar c) {
         for(;;) {
             if((cs=*s)==c) {
                 return (UChar *)s;
+/* Maximum recursion depth for u_unescapeAt to prevent stack overflow */
+#define MAX_UNESCAPE_RECURSION_DEPTH 100
+
+/* Internal helper function with recursion depth tracking */
+static UChar32 u_unescapeAtInternal(UNESCAPE_CHAR_AT charAt,
+                                   int32_t *offset,
+                                   int32_t length,
+                                   void *context,
+                                   int32_t depth);
             }
