index 17d92ed1a3d..b3d1c6ee536 100644
--- a/icu4c/source/i18n/chnsecal.cpp
+++ b/icu4c/source/i18n/chnsecal.cpp
@@ -233,7 +233,24 @@ int32_t ChineseCalendar::handleGetExtendedYear(UErrorCode& status) {
