diff --git a/icu4c/source/i18n/calendar.cpp b/icu4c/source/i18n/calendar.cpp
index 5ef818b2fbe..65d4b13abab 100644
--- a/icu4c/source/i18n/calendar.cpp
+++ b/icu4c/source/i18n/calendar.cpp
@@ -2129,7 +2129,23 @@ void Calendar::add(UCalendarDateFields field, int32_t amount, UErrorCode& status

     switch (field) {
     case UCAL_ERA:
-        set(field, get(field, status) + amount);
+        {
+            int32_t currentValue = get(field, status);
+            if (U_FAILURE(status)) return;
+            // Check for overflow before addition
+            if ((amount > 0 && currentValue > INT32_MAX - amount) ||
+                (amount < 0 && currentValue < INT32_MIN - amount)) {
+                status = U_ILLEGAL_ARGUMENT_ERROR;
+                return;
+            }
+            int32_t newValue = currentValue + amount;
+            // Additional bounds check for reasonable era values
+            if (newValue < 0 || newValue > 1000) {
+                status = U_ILLEGAL_ARGUMENT_ERROR;
+                return;
+            }
+            set(field, newValue);
+        }
         pinField(UCAL_ERA, status);
         return;

@@ -2147,6 +2163,11 @@ void Calendar::add(UCalendarDateFields field, int32_t amount, UErrorCode& status
         if (era == 0) {
           const char * calType = getType();
           if ( uprv_strcmp(calType,"gregorian")==0 || uprv_strcmp(calType,"roc")==0 || uprv_strcmp(calType,"coptic")==0 ) {
+            // Check for negation overflow (INT32_MIN cannot be negated)
+            if (amount == INT32_MIN) {
+              status = U_ILLEGAL_ARGUMENT_ERROR;
+              return;
+            }
             amount = -amount;
           }
         }
@@ -2159,7 +2180,23 @@ void Calendar::add(UCalendarDateFields field, int32_t amount, UErrorCode& status
       {
         UBool oldLenient = isLenient();
         setLenient(true);
-        set(field, get(field, status) + amount);
+        {
+            int32_t currentValue = get(field, status);
+            if (U_FAILURE(status)) return;
+            // Check for overflow before addition
+            if ((amount > 0 && currentValue > INT32_MAX - amount) ||
+                (amount < 0 && currentValue < INT32_MIN - amount)) {
+                status = U_ILLEGAL_ARGUMENT_ERROR;
+                return;
+            }
+            int32_t newValue = currentValue + amount;
+            // Additional bounds check for reasonable year/month values
+            if (newValue < -1000000 || newValue > 1000000) {
+                status = U_ILLEGAL_ARGUMENT_ERROR;
+                return;
+            }
+            set(field, newValue);
+        }
         pinField(UCAL_DAY_OF_MONTH, status);
         if(oldLenient==false) {
           complete(status); /* force recalculate */

