patch:
diff --git a/icu4c/source/i18n/japancal.cpp b/icu4c/source/i18n/japancal.cpp
index 7e69b97a3af..fbe5b285457 100644
--- a/icu4c/source/i18n/japancal.cpp
+++ b/icu4c/source/i18n/japancal.cpp
@@ -228,7 +228,20 @@ void JapaneseCalendar::handleComputeFields(int32_t julianDay, UErrorCode& status
     int32_t eraIdx = gJapaneseEraRules->getEraIndex(year, internalGetMonth(status) + 1, internalGet(UCAL_DAY_OF_MONTH), status);

     internalSet(UCAL_ERA, eraIdx);
-    internalSet(UCAL_YEAR, year - gJapaneseEraRules->getStartYear(eraIdx, status) + 1);
+    
+    // Check for overflow in year calculation: year - startYear + 1
+    int32_t startYear = gJapaneseEraRules->getStartYear(eraIdx, status);
+    int32_t yearDiff;
+    int32_t finalYear;
+    
+    // Use 64-bit arithmetic to detect overflow in subtraction
+    int64_t yearDiff64 = static_cast<int64_t>(year) - static_cast<int64_t>(startYear);
+    yearDiff = static_cast<int32_t>(yearDiff64);
+    if (yearDiff64 != yearDiff || uprv_add32_overflow(yearDiff, 1, &finalYear)) {
+        status = U_ILLEGAL_ARGUMENT_ERROR;
+        return;
+    }
+    internalSet(UCAL_YEAR, finalYear);
 }

 /*
