diff --git a/icu4c/source/common/messagepattern.cpp b/icu4c/source/common/messagepattern.cpp
index 9e318295f9a..614b4fb2d03 100644
--- a/icu4c/source/common/messagepattern.cpp
+++ b/icu4c/source/common/messagepattern.cpp
@@ -30,6 +30,9 @@

 U_NAMESPACE_BEGIN

+// Maximum recursion depth to prevent stack overflow
+static const int32_t MAX_RECURSION_DEPTH = 100;
+
 // Unicode character/code point constants ---------------------------------- ***

 static const char16_t u_pound=0x23;
@@ -437,7 +440,7 @@ MessagePattern::parseMessage(int32_t index, int32_t msgStartLength,
     if(U_FAILURE(errorCode)) {
         return 0;
     }
-    if(nestingLevel>Part::MAX_VALUE) {
+    if(nestingLevel>MAX_RECURSION_DEPTH) {
         errorCode=U_INDEX_OUTOFBOUNDS_ERROR;
         return 0;
     }
