index 167d8644827..5f4051c95aa 100644
--- a/icu4c/source/i18n/calendar.cpp
+++ b/icu4c/source/i18n/calendar.cpp
@@ -3804,8 +3804,18 @@ int32_t Calendar::handleGetExtendedYearFromWeekFields(int32_t yearWoy, int32_t w

 int32_t Calendar::handleGetMonthLength(int32_t extendedYear, int32_t month, UErrorCode& status) const
 {
-    return handleComputeMonthStart(extendedYear, month+1, true, status) -
-        handleComputeMonthStart(extendedYear, month, true, status);
+    if (month == INT32_MAX) {
+        status = U_INTERNAL_PROGRAM_ERROR;
+        return 0;
+    }
+    int64_t startNext = handleComputeMonthStart(extendedYear, month+1, true, status);
+    int64_t startThis = handleComputeMonthStart(extendedYear, month, true, status);
+    int64_t diff = startNext - startThis;
+    if (diff < INT32_MIN || diff > INT32_MAX) {
+        status = U_INTERNAL_PROGRAM_ERROR;
+        return 0;
+    }
+    return static_cast<int32_t>(diff);
 }

