patch:
diff --git a/src/decNumber/decNumber.c b/src/decNumber/decNumber.c
index 5062634..3e583d0 100644
--- a/src/decNumber/decNumber.c
+++ b/src/decNumber/decNumber.c
@@ -6252,6 +6252,9 @@ static Int decUnitCompare(const Unit *a, Int alength,
     return 0;                      // all units match
     } // aligned
 
+  // Check for negative exponent which should not happen according to the function contract
+  if (exp < 0) return BADINT;      // invalid exponent
+
   // Unaligned.  If one is >1 unit longer than the other, padded
   // approximately, then can return easily
   if (alength>blength+(Int)D2U(exp)) return 1;
