5828| void SelectionDAG::Legalize() {
5829|   AssignTopologicalOrder();
5830| 
5831|   SmallPtrSet<SDNode *, 16> LegalizedNodes;
5832|   // Use a delete listener to remove nodes which were deleted during
5833|   // legalization from LegalizeNodes. This is needed to handle the situation
5834|   // where a new node is allocated by the object pool to the same address of a
5835|   // previously deleted node.
5836|   DAGNodeDeletedListener DeleteListener(
5837|       *this,
5838|       [&LegalizedNodes](SDNode *N, SDNode *E) { LegalizedNodes.erase(N); });
5839| 
5840|   SelectionDAGLegalize Legalizer(*this, LegalizedNodes);
5841| 
5842|   // Visit all the nodes. We start in topological order, so that we see
5843|   // nodes with their original operands intact. Legalization can produce
5844|   // new nodes which may themselves need to be legalized. Iterate until all
5845|   // nodes have been legalized.
5846|   while (true) {
5847|     bool AnyLegalized = false;
5848|     for (auto NI = allnodes_end(); NI != allnodes_begin();) {
5849|       --NI;
5850| 
5851|       SDNode *N = &*NI;
5852|       if (N->use_empty() && N != getRoot().getNode()) {
5853|         ++NI;
5854|         DeleteNode(N);
5855|         continue;
5856|       }
5857| 
5858|       if (LegalizedNodes.insert(N).second) {
5859|         AnyLegalized = true;
5860|         Legalizer.LegalizeOp(N);
5861| 
5862|         if (N->use_empty() && N != getRoot().getNode()) {
5863|           ++NI;
5864|           DeleteNode(N);
5865|         }
5866|       }
5867|     }
5868|     if (!AnyLegalized)
5869|       break;
5870| 
5871|   }
5872| 
5873|   // Remove dead nodes now.
5874|   RemoveDeadNodes();
5875| }
