patch:
diff --git a/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp b/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
index 6027572f1e84..6c2d0dd7d169 100644
--- a/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
+++ b/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
@@ -2127,7 +2127,12 @@ void VPScalarIVStepsRecipe::execute(VPTransformState &State) {
           Builder.CreateMul(StartIdx0, ConstantInt::get(StartIdx0->getType(),
                                                         getUnrollPart(*this)));
     }
-    StartIdx0 = Builder.CreateTrunc(StartIdx0, IntStepTy);
+    // Only truncate if the source type is actually larger than the destination
+    if (StartIdx0->getType()->getScalarSizeInBits() > IntStepTy->getScalarSizeInBits()) {
+      StartIdx0 = Builder.CreateTrunc(StartIdx0, IntStepTy);
+    } else if (StartIdx0->getType() != IntStepTy) {
+      StartIdx0 = Builder.CreateIntCast(StartIdx0, IntStepTy, /*isSigned=*/true);
+    }
   }

   if (!FirstLaneOnly && State.VF.isScalable()) {
