2048|   auto *StepC = dyn_cast<ConstantInt>(getStepValue()->getLiveInIRValue());
2049|   auto *StartC = dyn_cast<ConstantInt>(getStartValue()->getLiveInIRValue());
2050|   auto *CanIV = cast<VPCanonicalIVPHIRecipe>(&*getParent()->begin());
2051|   return StartC && StartC->isZero() && StepC && StepC->isOne() &&
2052|          getScalarType() == CanIV->getScalarType();
