+    StartIdx0 = State.get(getOperand(2), true);
+    if (getUnrollPart(*this) != 1) {
+      StartIdx0 =
+          Builder.CreateMul(StartIdx0, ConstantInt::get(StartIdx0->getType(),
+                                                        getUnrollPart(*this)));
+    }
+    // Only perform truncation if it is valid.
+    Type *SrcTy = nullptr;
+    SrcTy = StartIdx0->getType();
+    if (SrcTy->isIntegerTy() && IntStepTy->isIntegerTy() &&
+        SrcTy->getScalarSizeInBits() > IntStepTy->getScalarSizeInBits()) {
+      StartIdx0 = Builder.CreateTrunc(StartIdx0, IntStepTy);
+    } else if (SrcTy == IntStepTy) {
+      // No-op, types already match.
