diff --git a/clang/lib/Sema/SemaOpenMP.cpp b/clang/lib/Sema/SemaOpenMP.cpp
index 15d568fcb9cc..f1bc8e93fa24 100644
--- a/clang/lib/Sema/SemaOpenMP.cpp
+++ b/clang/lib/Sema/SemaOpenMP.cpp
@@ -10425,6 +10425,10 @@ StmtResult SemaOpenMP::ActOnOpenMPSimdDirective(
   if (NestedLoopCount == 0)
     return StmtError();

+  // If loop expressions were not built, bail out early to avoid assertion failure.
+  if (!B.builtAll())
+    return StmtError();
+
   if (finishLinearClauses(SemaRef, Clauses, B, DSAStack))
     return StmtError();

