     return StmtError();

-  if (finishLinearClauses(SemaRef, Clauses, B, DSAStack))
-    return StmtError();
+  // If loop expressions were not built, bail out early to avoid assertion failure.
+  if (!B.builtAll())
+    return StmtError();
+
+  if (finishLinearClauses(SemaRef, Clauses, B, DSAStack))
+    return StmtError();

   auto *ForDirective = OMPForDirective::Create(
       getASTContext(), StartLoc, EndLoc, NestedLoopCount, Clauses, AStmt, B,
       DSAStack->getTaskgroupReductionRef(), DSAStack->isCancelRegion());
   return ForDirective;
 }
+
@@ -10482,10 +10494,16 @@ StmtResult SemaOpenMP::ActOnOpenMPForSimdDirective(
   if (NestedLoopCount == 0)
     return StmtError();

-  if (finishLinearClauses(SemaRef, Clauses, B, DSAStack))
-    return StmtError();
+  // If loop expressions were not built, bail out early to avoid assertion failure.
+  if (!B.builtAll())
+    return StmtError();
+
+  if (finishLinearClauses(SemaRef, Clauses, B, DSAStack))
+    return StmtError();

