     return StmtError();

+  // Check collapse/ordered clause argument validity before proceeding.
+  Expr *CollapseExpr = getCollapseNumberExpr(Clauses);
+  if (CollapseExpr) {
+    if (CollapseExpr->isValueDependent() || CollapseExpr->isTypeDependent() ||
+        CollapseExpr->isInstantiationDependent() ||
+        CollapseExpr->containsUnexpandedParameterPack()) {
