patch:
diff --git a/clang/lib/AST/ASTContext.cpp b/clang/lib/AST/ASTContext.cpp
index 92b246a83bce..b06c9d7e464c 100644
--- a/clang/lib/AST/ASTContext.cpp
+++ b/clang/lib/AST/ASTContext.cpp
@@ -2339,6 +2339,23 @@ TypeInfo ASTContext::getTypeInfoImpl(const Type *T) const {
       Width = Target->getPointerWidth(LangAS::Default);
       Align = Target->getPointerAlign(LangAS::Default);
       break;
+    // Placeholder types - these should not normally be used for size calculations
+    // but we provide reasonable defaults to avoid crashes
+    case BuiltinType::Overload:
+    case BuiltinType::BoundMember:
+    case BuiltinType::UnresolvedTemplate:
+    case BuiltinType::PseudoObject:
+    case BuiltinType::UnknownAny:
+    case BuiltinType::BuiltinFn:
+    case BuiltinType::ARCUnbridgedCast:
+    case BuiltinType::IncompleteMatrixIdx:
+    case BuiltinType::ArraySection:
+    case BuiltinType::OMPArrayShaping:
+    case BuiltinType::OMPIterator:
+    case BuiltinType::Dependent:
+      Width = Target->getIntWidth();
+      Align = Target->getIntAlign();
+      break;
     }
     break;
   case Type::ObjCObjectPointer:
diff --git a/clang/lib/Sema/SemaOpenMP.cpp b/clang/lib/Sema/SemaOpenMP.cpp
index 835dba22a858..a0559dfb8e0c 100644
--- a/clang/lib/Sema/SemaOpenMP.cpp
+++ b/clang/lib/Sema/SemaOpenMP.cpp
@@ -14348,6 +14348,12 @@ StmtResult SemaOpenMP::ActOnOpenMPTileDirective(ArrayRef<OMPClause *> Clauses,
     //   (TS <= 0) ? 1 : TS
     // \endcode
     QualType DimTy = DimTileSizeExpr->getType();
+    // If DimTy is not a proper integer type (e.g., a placeholder type),
+    // use int as a fallback to avoid crashes
+    if (!DimTy->isIntegerType()) {
+      DimTy = Context.IntTy;
+    }
+    
     uint64_t DimWidth = Context.getTypeSize(DimTy);
     IntegerLiteral *Zero = IntegerLiteral::Create(
         Context, llvm::APInt::getZero(DimWidth), DimTy, {});
