#define ea_each(ea, size, entry_var, code)                                     \
  do {                                                                         \
    hash_entry *entry_var = ea;                                                \
    uint32_t size__ = size;                                                    \
    for (; 0 < size__; ++entry_var) {                                          \
      if (entry_deleted_p(entry_var))                                          \
        continue;                                                              \
      --size__;                                                                \
      code;                                                                    \
    }                                                                          \
  } while (0)

// Expands to
do {
  hash_entry *r_entry = ea;
  uint32_t size__ = size;
  for (; 0 < size__; ++r_entry) {
    if (entry_deleted_p(r_entry))
      continue;
    --size__;
    {
      do {
        index_buckets_iter it[1];
        ib_it_init(mrb, it, h, r_entry->key);
        for (;;) {
          ib_it_next(it);
          {
            if (ib_it_active_p(it)) {
              if (!obj_eql(mrb, r_entry->key, ib_it_entry(it)->key, h))
                continue;
              ib_it_entry(it)->val = r_entry->val;
              ht_set_size(h, --size);
              entry_delete(r_entry);
            } else {
              if (w_size != ((uint32_t)(r_entry - ea))) {
                ea_set(ea, w_size, r_entry->key, r_entry->val);
                entry_delete(r_entry);
              }
              ib_it_set(it, w_size++);
            }
            break;
          };
        }
      } while (0);
    };
  }
} while (0)
