- After deleting `curLayer`, if `m_LastLayer` is `nullptr`, skip the trailer logic (lines 92-107).
- This prevents dereferencing a null pointer in the trailer logic.

Let's update the patch accordingly.

args.patch:
diff --git a/Packet++/src/Packet.cpp b/Packet++/src/Packet.cpp
index 30a2790f..af6b725b 100644
--- a/Packet++/src/Packet.cpp
+++ b/Packet++/src/Packet.cpp
@@ -84,9 +84,17 @@ namespace pcpp

 		if (curLayer != nullptr && curLayer->getOsiModelLayer() > parseUntilLayer)
 		{
-			m_LastLayer = curLayer->getPrevLayer();
+			Layer* prevLayer = curLayer->getPrevLayer();
+			if (prevLayer != nullptr)
+			{
+				prevLayer->m_NextLayer = nullptr;
+			}
+			if (curLayer == m_FirstLayer)
+			{
+				m_FirstLayer = nullptr;
+			}
 			delete curLayer;
-			m_LastLayer->m_NextLayer = nullptr;
+			m_LastLayer = prevLayer;
 		}

 		if (m_LastLayer != nullptr && parseUntil == UnknownProtocol && parseUntilLayer == OsiModelLayerUnknown)
