patch:
diff --git a/Zend/zend_opcode.c b/Zend/zend_opcode.c
index 97110cf54c0..2753b404bf8 100644
--- a/Zend/zend_opcode.c
+++ b/Zend/zend_opcode.c
@@ -1071,8 +1071,13 @@ ZEND_API void pass_two(zend_op_array *op_array)
 				}
 				break;
 			case ZEND_FAST_CALL:
-				opline->op1.opline_num = op_array->try_catch_array[opline->op1.num].finally_op;
-				ZEND_PASS_TWO_UPDATE_JMP_TARGET(op_array, opline, opline->op1);
+				if (op_array->try_catch_array != NULL && opline->op1.num < op_array->last_try_catch) {
+					opline->op1.opline_num = op_array->try_catch_array[opline->op1.num].finally_op;
+					ZEND_PASS_TWO_UPDATE_JMP_TARGET(op_array, opline, opline->op1);
+				} else {
+					/* Defensive: make this a NOP if invalid */
+					MAKE_NOP(opline);
+				}
 				break;
 			case ZEND_BRK:
 			case ZEND_CONT:
