patch:
diff --git a/ext/pdo_sqlite/sqlite_driver.c b/ext/pdo_sqlite/sqlite_driver.c
index 2f494c2ddb8..6f84592c399 100644
--- a/ext/pdo_sqlite/sqlite_driver.c
+++ b/ext/pdo_sqlite/sqlite_driver.c
@@ -434,19 +434,28 @@ static int do_callback(struct pdo_sqlite_fci *fc, zval *cb,
 		}

 		if (agg_context) {
-			zval_ptr_dtor(&agg_context->val);
+            if (!Z_ISUNDEF(agg_context->val)) {
+                zval_ptr_dtor(&agg_context->val);
+                ZVAL_UNDEF(&agg_context->val);
+            }
 		}
 	} else {
 		/* we're stepping in an aggregate; the return value goes into
 		 * the context */
 		if (agg_context) {
-			if (Z_ISUNDEF(retval)) {
-				zval_ptr_dtor(&agg_context->val);
-				return FAILURE;
-			}
-			zval_ptr_dtor(Z_REFVAL(agg_context->val));
-			ZVAL_COPY_VALUE(Z_REFVAL(agg_context->val), &retval);
-			ZVAL_UNDEF(&retval);
+                if (Z_ISUNDEF(retval)) {
+                    if (!Z_ISUNDEF(agg_context->val)) {
+                        zval_ptr_dtor(&agg_context->val);
+                        ZVAL_UNDEF(&agg_context->val);
+                    }
+                    return FAILURE;
+                }
+                if (!Z_ISUNDEF(agg_context->val)) {
+                    zval_ptr_dtor(Z_REFVAL(agg_context->val));
+                    ZVAL_UNDEF(Z_REFVAL(agg_context->val));
+                }
+                ZVAL_COPY_VALUE(Z_REFVAL(agg_context->val), &retval);
+                ZVAL_UNDEF(&retval);
 		}
 	}
