+++ b/Zend/zend_execute.h
@@ -288,7 +288,9 @@ static zend_always_inline void zend_vm_stack_free_args(zend_execute_data *call)
 		zval *p = ZEND_CALL_ARG(call, 1);

 		do {
-			zval_ptr_dtor_nogc(p);
+            if (EXPECTED(Z_TYPE_P(p) != IS_UNDEF)) {
+                zval_ptr_dtor_nogc(p);
+            }
 			p++;
 		} while (--num_args);
