164| 				if (UNEXPECTED(GC_MAY_LEAK(garbage))) {
165| 					gc_possible_root(garbage);
166| 				}
167| 			}
168| 			return variable_ptr;
169| 		}
170| 	} while (0);
171| 
172| 	zend_copy_to_variable(variable_ptr, value, value_type);
173| 	return variable_ptr;
174| }
