diff --git a/Zend/zend_types.h b/Zend/zend_types.h
index e6505d8b62a..cbeaa34519e 100644
--- a/Zend/zend_types.h
+++ b/Zend/zend_types.h
@@ -1260,8 +1260,10 @@ static zend_always_inline uint32_t zval_addref_p(zval* pz) {
 }

 static zend_always_inline uint32_t zval_delref_p(zval* pz) {
-	ZEND_ASSERT(Z_REFCOUNTED_P(pz));
-	return GC_DELREF(Z_COUNTED_P(pz));
+    if (pz == NULL || !Z_REFCOUNTED_P(pz)) {
+        return 0;
+    }
+    return GC_DELREF(Z_COUNTED_P(pz));
 }

