+	if (intern->ptr != NULL) {
+		php_libxml_node_ptr *node_ptr = (php_libxml_node_ptr *)intern->ptr;
+		/* If the node pointer is NULL, the node was freed during xinclude processing, skip cleanup */
+		if (node_ptr->node != NULL) {
