diff --git a/Zend/zend_builtin_functions.c b/Zend/zend_builtin_functions.c
index 245552d0946..f7c8e100a17 100644
--- a/Zend/zend_builtin_functions.c
+++ b/Zend/zend_builtin_functions.c
@@ -729,10 +729,17 @@ static void add_class_vars(zend_class_entry *scope, zend_class_entry *ce, bool s
 		}
 		prop = NULL;
 		if (statics && (prop_info->flags & ZEND_ACC_STATIC) != 0) {
-			prop = &ce->default_static_members_table[prop_info->offset];
-			ZVAL_DEINDIRECT(prop);
+			if (ce->default_static_members_table != NULL &&
+			    prop_info->offset < ce->default_static_members_count) {
+				prop = &ce->default_static_members_table[prop_info->offset];
+				ZVAL_DEINDIRECT(prop);
+			}
 		} else if (!statics && (prop_info->flags & ZEND_ACC_STATIC) == 0) {
-			prop = &default_properties_table[OBJ_PROP_TO_NUM(prop_info->offset)];
+			int num = OBJ_PROP_TO_NUM(prop_info->offset);
+			if (default_properties_table != NULL &&
+			    num >= 0 && num < ce->default_properties_count) {
+				prop = &default_properties_table[num];
+			}
 		}
 		if (!prop) {
 			continue;
