patch:
diff --git a/ext/dom/dom_iterators.c b/ext/dom/dom_iterators.c
index 866cb5a07e9..cbc1ffe5d64 100644
--- a/ext/dom/dom_iterators.c
+++ b/ext/dom/dom_iterators.c
@@ -187,8 +187,14 @@ static void php_dom_iterator_move_forward(zend_object_iterator *iter) /* {{{ */

 	object = &iterator->intern.data;
 	nnmap = Z_DOMOBJ_P(object);
+	if (nnmap == NULL || nnmap->ptr == NULL) {
+		goto err;
+	}
 	objmap = (dom_nnodemap_object *)nnmap->ptr;

+	if (Z_TYPE(iterator->curobj) != IS_OBJECT) {
+		goto err;
+	}
 	intern = Z_DOMOBJ_P(&iterator->curobj);

 	if (intern != NULL && intern->ptr != NULL) {
@@ -237,7 +243,7 @@ static void php_dom_iterator_move_forward(zend_object_iterator *iter) /* {{{ */
 		ZVAL_UNDEF(&iterator->curobj);
 	}
 	if (curnode) {
-		php_dom_create_object(curnode, &iterator->curobj, objmap->baseobj);
+		php_dom_create_object(curnode, &iterator->curobj, objmap ? objmap->baseobj : NULL);
 	}
 }
 /* }}} */
