patch:
diff --git a/ext/reflection/php_reflection.c b/ext/reflection/php_reflection.c
index 531db004b3b..dfe7deed1c5 100644
--- a/ext/reflection/php_reflection.c
+++ b/ext/reflection/php_reflection.c
@@ -6505,6 +6505,10 @@ ZEND_METHOD(ReflectionProperty, getHooks)

 	GET_REFLECTION_OBJECT_PTR(ref);

+	if (ref->prop == NULL) {
+		RETURN_EMPTY_ARRAY();
+	}
+
 	if (!ref->prop->hooks) {
 		RETURN_EMPTY_ARRAY();
 	}
@@ -6536,6 +6540,10 @@ ZEND_METHOD(ReflectionProperty, getHook)

 	GET_REFLECTION_OBJECT_PTR(ref);

+	if (ref->prop == NULL) {
+		RETURN_NULL();
+	}
+
 	zend_function *hook;
 	if (zend_string_equals_literal(Z_STR_P(zend_enum_fetch_case_name(type)), "Get")) {
 		hook = ref->prop->hooks ? ref->prop->hooks[ZEND_PROPERTY_HOOK_GET] : NULL;
