patch:
diff --git a/Zend/zend_object_handlers.c b/Zend/zend_object_handlers.c
index 64e494200ee..5e4e3c34430 100644
--- a/Zend/zend_object_handlers.c
+++ b/Zend/zend_object_handlers.c
@@ -808,8 +808,12 @@ ZEND_API zval *zend_std_read_property(zend_object *zobj, zend_string *name, int

 		zend_class_entry *ce = zobj->ce;

-		if (!zend_call_get_hook(prop_info, name, get, zobj, rv)) {
+		GC_ADDREF(zobj);
+		bool hook_result = zend_call_get_hook(prop_info, name, get, zobj, rv);
+		/* zend_call_get_hook or the getter it calls may have triggered object destruction */
+		if (!hook_result) {
 			if (EG(exception)) {
+				OBJ_RELEASE(zobj);
 				return &EG(uninitialized_zval);
 			}

@@ -823,6 +827,7 @@ ZEND_API zval *zend_std_read_property(zend_object *zobj, zend_string *name, int
 			if (!ZEND_TYPE_IS_SET(prop_info->type)) {
 				prop_info = NULL;
 			}
+			OBJ_RELEASE(zobj);
 			goto try_again;
 		}

@@ -846,7 +851,7 @@ ZEND_API zval *zend_std_read_property(zend_object *zobj, zend_string *name, int
 		} else {
 			retval = &EG(uninitialized_zval);
 		}
-
+		OBJ_RELEASE(zobj);
 		goto exit;
 	} else if (UNEXPECTED(EG(exception))) {
 		retval = &EG(uninitialized_zval);
