 	}

 	if (child->doc == NULL && parentp->doc != NULL) {
-		dom_set_document_pointers(child, parentp->doc, intern->document);
+		xmlSetTreeDoc(child, parentp->doc);
+		if (intern->document) {
+			/* Set PHP document references only if we have a valid document */
