index 3f2e517f3fe..914062a9922 100644
--- a/Zend/zend_weakrefs.c
+++ b/Zend/zend_weakrefs.c
@@ -534,7 +534,12 @@ static void zend_weakmap_iterator_get_current_key(zend_object_iterator *obj_iter
 		ZEND_ASSERT(0 && "Must have integer key");
 	}

-	ZVAL_OBJ_COPY(key, zend_weakref_key_to_object(num_key));
+    zend_object *obj = zend_weakref_key_to_object(num_key);
+    if (obj) {
+        ZVAL_OBJ_COPY(key, obj);
+    } else {
+        ZVAL_NULL(key);
