+			new_elem->prev = element->prev;
+			new_elem->next = element->next;
+			ZVAL_COPY(&new_elem->data, value);
+			if (element->prev) {
+				element->prev->next = new_elem;
+			} else {
+				intern->llist->head = new_elem;
