patch:
diff --git a/ext/spl/spl_fixedarray.c b/ext/spl/spl_fixedarray.c
index 7c08a189c6f..0921d87f3ae 100644
--- a/ext/spl/spl_fixedarray.c
+++ b/ext/spl/spl_fixedarray.c
@@ -484,8 +484,14 @@ static void spl_fixedarray_object_unset_dimension_helper(spl_fixedarray_object *
 		return;
 	} else {
 		intern->array.should_rebuild_properties = true;
-		zval_ptr_dtor(&(intern->array.elements[index]));
-		ZVAL_NULL(&intern->array.elements[index]);
+		if (intern->array.elements) {
+			zval tmp;
+			ZVAL_COPY_VALUE(&tmp, &intern->array.elements[index]);
+			ZVAL_NULL(&intern->array.elements[index]);
+			zval_ptr_dtor(&tmp);
+		} else {
+			/* Array elements have been freed, nothing to do */
+		}
 	}
 }
