index c9123ab14df..c87d6877bab 100644
--- a/ext/dom/php_dom.c
+++ b/ext/dom/php_dom.c
@@ -985,9 +985,12 @@ void dom_objects_free_storage(zend_object *object)
 	zend_object_std_dtor(&intern->std);

 	if (intern->ptr != NULL && ((php_libxml_node_ptr *)intern->ptr)->node != NULL) {
