index 5f9749ffed2..835eb48247d 100644
--- a/ext/libxml/libxml.c
+++ b/ext/libxml/libxml.c
@@ -1305,10 +1305,13 @@ PHP_LIBXML_API void php_libxml_node_decrement_resource(php_libxml_node_object *o
 				obj_node->_private = NULL;
 			}
