@@ -771,6 +771,8 @@ PHP_METHOD(SplObjectStorage, serialize)
 	spl_SplObjectStorage *intern = Z_SPLOBJSTORAGE_P(ZEND_THIS);

 	spl_SplObjectStorageElement *element;
+	spl_SplObjectStorageElement *elements_copy = NULL;
+	uint32_t num_elements = 0;
 	zval members, flags;
 	HashPosition      pos;
 	php_serialize_data_t var_hash;
@@ -784,24 +786,46 @@ PHP_METHOD(SplObjectStorage, serialize)

 	/* storage */
 	smart_str_appendl(&buf, "x:", 2);
-	ZVAL_LONG(&flags, zend_hash_num_elements(&intern->storage));
+	num_elements = zend_hash_num_elements(&intern->storage);
+	ZVAL_LONG(&flags, num_elements);
 	php_var_serialize(&buf, &flags, &var_hash);

-	zend_hash_internal_pointer_reset_ex(&intern->storage, &pos);
+	/* Create a copy of all elements to avoid use-after-free during serialization */
+	if (num_elements > 0) {
+		elements_copy = emalloc(sizeof(spl_SplObjectStorageElement) * num_elements);
+		uint32_t i = 0;
+		
+		zend_hash_internal_pointer_reset_ex(&intern->storage, &pos);
+		while (zend_hash_has_more_elements_ex(&intern->storage, &pos) == SUCCESS && i < num_elements) {
+			if ((element = zend_hash_get_current_data_ptr_ex(&intern->storage, &pos)) == NULL) {
+				break;
+			}
+			elements_copy[i].obj = element->obj;
+			GC_ADDREF(element->obj);
+			ZVAL_COPY(&elements_copy[i].inf, &element->inf);
+			i++;
+			zend_hash_move_forward_ex(&intern->storage, &pos);
+		}
+		num_elements = i; /* Update in case we had fewer elements than expected */
+	}

-	while (zend_hash_has_more_elements_ex(&intern->storage, &pos) == SUCCESS) {
+	/* Serialize using the copied elements */
+	for (uint32_t i = 0; i < num_elements; i++) {
 		zval obj;
-		if ((element = zend_hash_get_current_data_ptr_ex(&intern->storage, &pos)) == NULL) {
-			smart_str_free(&buf);
-			PHP_VAR_SERIALIZE_DESTROY(var_hash);
-			RETURN_NULL();
-		}
-		ZVAL_OBJ(&obj, element->obj);
+		ZVAL_OBJ(&obj, elements_copy[i].obj);
 		php_var_serialize(&buf, &obj, &var_hash);
 		smart_str_appendc(&buf, ',');
-		php_var_serialize(&buf, &element->inf, &var_hash);
+		php_var_serialize(&buf, &elements_copy[i].inf, &var_hash);
 		smart_str_appendc(&buf, ';');
-		zend_hash_move_forward_ex(&intern->storage, &pos);
+	}
+
+	/* Clean up the copied elements */
+	for (uint32_t i = 0; i < num_elements; i++) {
+		zend_object_release(elements_copy[i].obj);
+		zval_ptr_dtor(&elements_copy[i].inf);
+	}
+	if (elements_copy) {
+		efree(elements_copy);
 	}

 	/* members */

