index cc2956ccc27..b0bf49ddd56 100644
--- a/ext/spl/spl_observer.c
+++ b/ext/spl/spl_observer.c
@@ -788,20 +788,29 @@ PHP_METHOD(SplObjectStorage, serialize)
 	php_var_serialize(&buf, &flags, &var_hash);

 	zend_hash_internal_pointer_reset_ex(&intern->storage, &pos);
-
-	while (zend_hash_has_more_elements_ex(&intern->storage, &pos) == SUCCESS) {
-		zval obj;
-		if ((element = zend_hash_get_current_data_ptr_ex(&intern->storage, &pos)) == NULL) {
-			smart_str_free(&buf);
-			PHP_VAR_SERIALIZE_DESTROY(var_hash);
-			RETURN_NULL();
+	/* Deep copy elements to avoid use-after-free if storage is modified during serialization */
+	uint32_t element_count = zend_hash_num_elements(&intern->storage);
+	spl_SplObjectStorageElement *elements_copy = NULL;
+	if (element_count) {
+		elements_copy = emalloc(sizeof(spl_SplObjectStorageElement) * element_count);
+		uint32_t i = 0;
+		ZEND_HASH_FOREACH_PTR(&intern->storage, element) {
+			elements_copy[i].obj = element->obj;
+			GC_ADDREF(element->obj);
+			ZVAL_COPY(&elements_copy[i].inf, &element->inf);
+			i++;
+		} ZEND_HASH_FOREACH_END();
+		for (uint32_t j = 0; j < element_count; ++j) {
+			zval obj;
+			ZVAL_OBJ(&obj, elements_copy[j].obj);
+			php_var_serialize(&buf, &obj, &var_hash);
+			smart_str_appendc(&buf, ',');
+			php_var_serialize(&buf, &elements_copy[j].inf, &var_hash);
+			smart_str_appendc(&buf, ';');
+			zval_ptr_dtor(&elements_copy[j].inf);
+			zend_object_release(elements_copy[j].obj);
 		}
-		ZVAL_OBJ(&obj, element->obj);
-		php_var_serialize(&buf, &obj, &var_hash);
-		smart_str_appendc(&buf, ',');
-		php_var_serialize(&buf, &element->inf, &var_hash);
-		smart_str_appendc(&buf, ';');
-		zend_hash_move_forward_ex(&intern->storage, &pos);
+		efree(elements_copy);
 	}

