responded: Here is the patch to fix the use-after-free in `zim_SplObjectStorage_serialize` by collecting pointers to the elements before iterating and serializing, thus avoiding accessing freed memory if the storage is modified during serialization.

```diff
--- a/ext/spl/spl_observer.c
+++ b/ext/spl/spl_observer.c
@@ -790,17 +790,44 @@
 	zend_hash_internal_pointer_reset_ex(&intern->storage, &pos);
-
-	while (zend_hash_has_more_elements_ex(&intern->storage, &pos) == SUCCESS) {
-		zval obj;
-		if ((element = zend_hash_get_current_data_ptr_ex(&intern->storage, &pos)) == NULL) {
-			smart_str_free(&buf);
-			PHP_VAR_SERIALIZE_DESTROY(var_hash);
-			RETURN_NULL();
-		}
-		ZVAL_OBJ(&obj, element->obj);
-		php_var_serialize(&buf, &obj, &var_hash);
-		smart_str_appendc(&buf, ',');
-		php_var_serialize(&buf, &element->inf, &var_hash);
-		smart_str_appendc(&buf, ';');
-		zend_hash_move_forward_ex(&intern->storage, &pos);
-	}
+	/* To avoid use-after-free if storage is modified during serialization,
+	   collect pointers to elements first. */
+	spl_SplObjectStorageElement **element_ptrs = NULL;
+	uint32_t element_count = zend_hash_num_elements(&intern->storage);
+	uint32_t i = 0;
+	if (element_count) {
+		element_ptrs = emalloc(sizeof(spl_SplObjectStorageElement*) * element_count);
+		ZEND_HASH_FOREACH_PTR(&intern->storage, element) {
+			element_ptrs[i++] = element;
+		} ZEND_HASH_FOREACH_END();
+	}
+	for (i = 0; i < element_count; ++i) {
+		zval obj;
+		element = element_ptrs[i];
+		if (!element) {
+			smart_str_free(&buf);
+			PHP_VAR_SERIALIZE_DESTROY(var_hash);
+			efree(element_ptrs);
+			RETURN_NULL();
+		}
+		ZVAL_OBJ(&obj, element->obj);
+		php_var_serialize(&buf, &obj, &var_hash);
+		smart_str_appendc(&buf, ',');
+		php_var_serialize(&buf, &element->inf, &var_hash);
+		smart_str_appendc(&buf, ';');
+	}
+	if (element_ptrs) {
+		efree(element_ptrs);
+	}
