responded: Here is the patch to fix the use-after-free in `zim_SplObjectStorage_serialize` by making a shallow copy of the element pointers before iterating, thus preventing use-after-free if the storage is modified during serialization:

```diff
--- a/ext/spl/spl_observer.c
+++ b/ext/spl/spl_observer.c
@@ -790,13 +790,38 @@
 	zend_hash_internal_pointer_reset_ex(&intern->storage, &pos);
-
-	while (zend_hash_has_more_elements_ex(&intern->storage, &pos) == SUCCESS) {
-		zval obj;
-		if ((element = zend_hash_get_current_data_ptr_ex(&intern->storage, &pos)) == NULL) {
-			smart_str_free(&buf);
-			PHP_VAR_SERIALIZE_DESTROY(var_hash);
-			RETURN_NULL();
-		}
-		ZVAL_OBJ(&obj, element->obj);
-		php_var_serialize(&buf, &obj, &var_hash);
-		smart_str_appendc(&buf, ',');
-		php_var_serialize(&buf, &element->inf, &var_hash);
-		smart_str_appendc(&buf, ';');
-		zend_hash_move_forward_ex(&intern->storage, &pos);
-	}
+	/* Make a shallow copy of the element pointers to avoid use-after-free if storage is modified during serialization */
+	int num_elements = zend_hash_num_elements(&intern->storage);
+	spl_SplObjectStorageElement **element_array = NULL;
+	if (num_elements > 0) {
+		element_array = emalloc(sizeof(spl_SplObjectStorageElement*) * num_elements);
+		int i = 0;
+		ZEND_HASH_FOREACH_PTR(&intern->storage, element) {
+			element_array[i++] = element;
+		} ZEND_HASH_FOREACH_END();
+	}
+	for (int i = 0; i < num_elements; ++i) {
+		zval obj;
+		element = element_array[i];
+		if (element == NULL) {
+			continue;
+		}
+		ZVAL_OBJ(&obj, element->obj);
+		php_var_serialize(&buf, &obj, &var_hash);
+		smart_str_appendc(&buf, ',');
+		php_var_serialize(&buf, &element->inf, &var_hash);
+		smart_str_appendc(&buf, ';');
+	}
+	if (element_array) {
+		efree(element_array);
+	}

 	/* members */
 	smart_str_appendl(&buf, "m:", 2);

 	ZVAL_ARR(&members, zend_array_dup(zend_std_get_properties(Z_OBJ_P(ZEND_THIS))));
 	php_var_serialize(&buf, &members, &var_hash); /* finishes the string */
 	zval_ptr_dtor(&members);

 	/* done */
 	PHP_VAR_SERIALIZE_DESTROY(var_hash);

 	RETURN_STR(smart_str_extract(&buf));
 }
