diff --git a/ext/spl/spl_observer.c b/ext/spl/spl_observer.c
index cc2956ccc27..6de917168ef 100644
--- a/ext/spl/spl_observer.c
+++ b/ext/spl/spl_observer.c
@@ -782,27 +782,47 @@ PHP_METHOD(SplObjectStorage, serialize)

 	PHP_VAR_SERIALIZE_INIT(var_hash);

-	/* storage */
-	smart_str_appendl(&buf, "x:", 2);
-	ZVAL_LONG(&flags, zend_hash_num_elements(&intern->storage));
-	php_var_serialize(&buf, &flags, &var_hash);
-
-	zend_hash_internal_pointer_reset_ex(&intern->storage, &pos);
-
-	while (zend_hash_has_more_elements_ex(&intern->storage, &pos) == SUCCESS) {
-		zval obj;
-		if ((element = zend_hash_get_current_data_ptr_ex(&intern->storage, &pos)) == NULL) {
-			smart_str_free(&buf);
-			PHP_VAR_SERIALIZE_DESTROY(var_hash);
-			RETURN_NULL();
-		}
-		ZVAL_OBJ(&obj, element->obj);
-		php_var_serialize(&buf, &obj, &var_hash);
-		smart_str_appendc(&buf, ',');
-		php_var_serialize(&buf, &element->inf, &var_hash);
-		smart_str_appendc(&buf, ';');
-		zend_hash_move_forward_ex(&intern->storage, &pos);
-	}
+    /* storage */
+    smart_str_appendl(&buf, "x:", 2);
+    ZVAL_LONG(&flags, zend_hash_num_elements(&intern->storage));
+    php_var_serialize(&buf, &flags, &var_hash);
+
+    /* Make a deep copy of inf zvals to avoid use-after-free */
+    size_t num_elements = zend_hash_num_elements(&intern->storage);
+    spl_SplObjectStorageElement **element_ptrs = NULL;
+    zval *inf_copies = NULL;
+    if (num_elements > 0) {
+        element_ptrs = emalloc(sizeof(spl_SplObjectStorageElement*) * num_elements);
+        inf_copies = emalloc(sizeof(zval) * num_elements);
+        size_t idx = 0;
+        ZEND_HASH_FOREACH_PTR(&intern->storage, element) {
+            element_ptrs[idx] = element;
+            ZVAL_COPY(&inf_copies[idx], &element->inf);
+            idx++;
+        } ZEND_HASH_FOREACH_END();
+    }
+
+    for (size_t i = 0; i < num_elements; ++i) {
+        zval obj;
+        element = element_ptrs[i];
+        if (element == NULL) {
+            continue;
+        }
+        ZVAL_OBJ(&obj, element->obj);
+        php_var_serialize(&buf, &obj, &var_hash);
+        smart_str_appendc(&buf, ',');
+        php_var_serialize(&buf, &inf_copies[i], &var_hash);
+        smart_str_appendc(&buf, ';');
+    }
+    if (inf_copies) {
+        for (size_t i = 0; i < num_elements; ++i) {
+            zval_ptr_dtor(&inf_copies[i]);
+        }
+        efree(inf_copies);
+    }
+    if (element_ptrs) {
+        efree(element_ptrs);
+    }

 	/* members */
 	smart_str_appendl(&buf, "m:", 2);

