index 6592efc4e5e..659d4aa762b 100644
--- a/ext/spl/spl_dllist.c
+++ b/ext/spl/spl_dllist.c
@@ -1005,7 +1005,8 @@ PHP_METHOD(SplDoublyLinkedList, serialize)
 {
 	spl_dllist_object     *intern   = Z_SPLDLLIST_P(ZEND_THIS);
 	smart_str              buf      = {0};
-	spl_ptr_llist_element *current  = intern->llist->head, *next;
+	spl_ptr_llist_element *current, *next;
+	spl_ptr_llist_element **elements = NULL;
 	zval                   flags;
 	php_serialize_data_t   var_hash;

@@ -1020,13 +1021,33 @@ PHP_METHOD(SplDoublyLinkedList, serialize)
 	php_var_serialize(&buf, &flags, &var_hash);

 	/* elements */
-	while (current) {
-		smart_str_appendc(&buf, ':');
-		next = current->next;
+	/* Create a snapshot of elements to prevent use-after-free during serialization */
+	int count = intern->llist->count;
+	if (count > 0) {
+		elements = emalloc(sizeof(spl_ptr_llist_element*) * count);
+		current = intern->llist->head;
+		int i = 0;
+		while (current && i < count) {
+			elements[i] = current;
+			SPL_LLIST_ADDREF(current);
+			current = current->next;
+			i++;
+		}
+		count = i; /* Actual count in case list was modified during snapshot */
+	}

-		php_var_serialize(&buf, &current->data, &var_hash);
+	/* Serialize elements from snapshot */
+	for (int i = 0; i < count; i++) {
+		smart_str_appendc(&buf, ':');
+		php_var_serialize(&buf, &elements[i]->data, &var_hash);
+	}

-		current = next;
+	/* Clean up snapshot */
+	if (elements) {
+		for (int i = 0; i < count; i++) {
+			SPL_LLIST_DELREF(elements[i]);
+		}
+		efree(elements);
 	}

 	/* done */

