-	smart_str              buf      = {0};
-	spl_ptr_llist_element *current  = intern->llist->head, *next;
-	zval                   flags;
-	php_serialize_data_t   var_hash;
+    smart_str              buf      = {0};
+    spl_ptr_llist_element *current  = intern->llist->head, *next;
+    zval                   flags;
+    php_serialize_data_t   var_hash;

 	if (zend_parse_parameters_none() == FAILURE) {
 		RETURN_THROWS();
@@ -1019,15 +1019,22 @@ PHP_METHOD(SplDoublyLinkedList, serialize)
 	ZVAL_LONG(&flags, intern->flags);
 	php_var_serialize(&buf, &flags, &var_hash);

-	/* elements */
-	while (current) {
-		smart_str_appendc(&buf, ':');
-		next = current->next;
+    /* elements */
+    /* Addref all elements first to avoid use-after-free */
