```diff
--- a/ext/spl/spl_dllist.c
+++ b/ext/spl/spl_dllist.c
@@ -1004,16 +1004,46 @@
 PHP_METHOD(SplDoublyLinkedList, serialize)
 {
     spl_dllist_object     *intern   = Z_SPLDLLIST_P(ZEND_THIS);
     smart_str              buf      = {0};
-    spl_ptr_llist_element *current  = intern->llist->head, *next;
+    spl_ptr_llist_element **snapshot = NULL;
+    int snapshot_count = 0, i = 0;
+    spl_ptr_llist_element *current = intern->llist->head;
     zval                   flags;
     php_serialize_data_t   var_hash;

     if (zend_parse_parameters_none() == FAILURE) {
         RETURN_THROWS();
     }

     PHP_VAR_SERIALIZE_INIT(var_hash);

     /* flags */
     ZVAL_LONG(&flags, intern->flags);
     php_var_serialize(&buf, &flags, &var_hash);

-    /* elements */
-    while (current) {
-        smart_str_appendc(&buf, ':');
-        next = current->next;
-
-        php_var_serialize(&buf, &current->data, &var_hash);
-
-        current = next;
-    }
+    /* Take a snapshot of all nodes */
+    for (current = intern->llist->head; current; current = current->next) {
+        snapshot_count++;
+    }
+    if (snapshot_count) {
+        snapshot = emalloc(sizeof(spl_ptr_llist_element*) * snapshot_count);
+        for (current = intern->llist->head, i = 0; current; current = current->next, i++) {
+            snapshot[i] = current;
+            SPL_LLIST_ADDREF(current);
+        }
+    }
+    for (i = 0; i < snapshot_count; i++) {
+        smart_str_appendc(&buf, ':');
+        php_var_serialize(&buf, &snapshot[i]->data, &var_hash);
+        SPL_LLIST_DELREF(snapshot[i]);
+    }
+    if (snapshot) {
+        efree(snapshot);
+    }

