patch:
diff --git a/ext/spl/spl_dllist.c b/ext/spl/spl_dllist.c
index 6592efc4e5e..ca314617994 100644
--- a/ext/spl/spl_dllist.c
+++ b/ext/spl/spl_dllist.c
@@ -1005,9 +1005,11 @@ PHP_METHOD(SplDoublyLinkedList, serialize)
 {
 	spl_dllist_object     *intern   = Z_SPLDLLIST_P(ZEND_THIS);
 	smart_str              buf      = {0};
-	spl_ptr_llist_element *current  = intern->llist->head, *next;
+	spl_ptr_llist_element *current  = intern->llist->head;
 	zval                   flags;
 	php_serialize_data_t   var_hash;
+	zval                  *elements = NULL;
+	int                    element_count = 0, i = 0;

 	if (zend_parse_parameters_none() == FAILURE) {
 		RETURN_THROWS();
@@ -1019,14 +1021,22 @@ PHP_METHOD(SplDoublyLinkedList, serialize)
 	ZVAL_LONG(&flags, intern->flags);
 	php_var_serialize(&buf, &flags, &var_hash);

-	/* elements */
-	while (current) {
-		smart_str_appendc(&buf, ':');
-		next = current->next;
-
-		php_var_serialize(&buf, &current->data, &var_hash);
-
-		current = next;
+	/* Copy elements to a temporary array to avoid use-after-free */
+	element_count = intern->llist->count;
+	if (element_count > 0) {
+		elements = safe_emalloc(element_count, sizeof(zval), 0);
+		current = intern->llist->head;
+		for (i = 0; i < element_count && current; ++i) {
+			ZVAL_COPY(&elements[i], &current->data);
+			current = current->next;
+		}
+		/* Serialize from the temporary array */
+		for (i = 0; i < element_count; ++i) {
+			smart_str_appendc(&buf, ':');
+			php_var_serialize(&buf, &elements[i], &var_hash);
+			zval_ptr_dtor(&elements[i]);
+		}
+		efree(elements);
 	}

 	/* done */

