patch:
diff --git a/ext/session/session.c b/ext/session/session.c
index b23f1fce358..040ecec00aa 100644
--- a/ext/session/session.c
+++ b/ext/session/session.c
@@ -1056,22 +1056,38 @@ PS_SERIALIZER_ENCODE_FUNC(php) /* {{{ */
 {
 	smart_str buf = {0};
 	php_serialize_data_t var_hash;
+	zval session_vars_copy;
 	PS_ENCODE_VARS;

 	PHP_VAR_SERIALIZE_INIT(var_hash);

-	PS_ENCODE_LOOP(
+	/* Create a copy of session variables to prevent use-after-free during serialization */
+	IF_SESSION_VARS() {
+		ZVAL_COPY(&session_vars_copy, Z_REFVAL(PS(http_session_vars)));
+	} else {
+		PHP_VAR_SERIALIZE_DESTROY(var_hash);
+		return NULL;
+	}
+
+	/* Use the copy for iteration to avoid use-after-free */
+	ZEND_HASH_FOREACH_KEY_VAL(Z_ARRVAL(session_vars_copy), num_key, key, struc) {
+		if (key == NULL) {
+			php_error_docref(NULL, E_WARNING, "Skipping numeric key " ZEND_LONG_FMT, num_key);
+			continue;
+		}
 		smart_str_appendl(&buf, ZSTR_VAL(key), ZSTR_LEN(key));
 		if (memchr(ZSTR_VAL(key), PS_DELIMITER, ZSTR_LEN(key))) {
+			zval_ptr_dtor(&session_vars_copy);
 			PHP_VAR_SERIALIZE_DESTROY(var_hash);
 			smart_str_free(&buf);
 			return NULL;
 		}
 		smart_str_appendc(&buf, PS_DELIMITER);
 		php_var_serialize(&buf, struc, &var_hash);
-	);
+	} ZEND_HASH_FOREACH_END();

 	smart_str_0(&buf);
+	zval_ptr_dtor(&session_vars_copy);

 	PHP_VAR_SERIALIZE_DESTROY(var_hash);
 	return buf.s;
