patch:
diff --git a/ext/session/session.c b/ext/session/session.c
index b23f1fce358..8f3ec5031bd 100644
--- a/ext/session/session.c
+++ b/ext/session/session.c
@@ -1056,23 +1056,46 @@ PS_SERIALIZER_ENCODE_FUNC(php) /* {{{ */
 {
 	smart_str buf = {0};
 	php_serialize_data_t var_hash;
-	PS_ENCODE_VARS;
+	zend_string *key;
+	zend_ulong num_key;
+	zval *struc;
+	HashTable *session_vars_copy;

 	PHP_VAR_SERIALIZE_INIT(var_hash);

-	PS_ENCODE_LOOP(
+	/* Create a copy of the session variables to avoid use-after-free during serialization */
+	IF_SESSION_VARS() {
+		HashTable *_ht = Z_ARRVAL_P(Z_REFVAL(PS(http_session_vars)));
+		ALLOC_HASHTABLE(session_vars_copy);
+		zend_hash_init(session_vars_copy, zend_hash_num_elements(_ht), NULL, ZVAL_PTR_DTOR, 0);
+		zend_hash_copy(session_vars_copy, _ht, (copy_ctor_func_t) zval_add_ref);
+	} else {
+		PHP_VAR_SERIALIZE_DESTROY(var_hash);
+		return NULL;
+	}
+
+	ZEND_HASH_FOREACH_KEY_VAL(session_vars_copy, num_key, key, struc) {
+		if (key == NULL) {
+			php_error_docref(NULL, E_WARNING,
+					"Skipping numeric key " ZEND_LONG_FMT, num_key);
+			continue;
+		}
 		smart_str_appendl(&buf, ZSTR_VAL(key), ZSTR_LEN(key));
 		if (memchr(ZSTR_VAL(key), PS_DELIMITER, ZSTR_LEN(key))) {
 			PHP_VAR_SERIALIZE_DESTROY(var_hash);
 			smart_str_free(&buf);
+			zend_hash_destroy(session_vars_copy);
+			FREE_HASHTABLE(session_vars_copy);
 			return NULL;
 		}
 		smart_str_appendc(&buf, PS_DELIMITER);
 		php_var_serialize(&buf, struc, &var_hash);
-	);
+	} ZEND_HASH_FOREACH_END();

 	smart_str_0(&buf);

+	zend_hash_destroy(session_vars_copy);
+	FREE_HASHTABLE(session_vars_copy);
 	PHP_VAR_SERIALIZE_DESTROY(var_hash);
 	return buf.s;
 }
